mapper.select:
select mc.* from bu_member_coupon mc,bu_coupon c where mc.coupon_id = c.id and c.coupon_type != '4' and c.rang = '4' and mc.`status` = '0' and mc.del_flag = '0' and mc.expiry_begin_date <= now() and mc.expiry_end_date > now() and mc.member_id = #{memberId}
and c.mcht_id = #{mchtId}
and mc.coupon_id in
#{couponId}

select mc.* from bu_member_coupon mc left join bu_coupon c on mc.coupon_id = c.id where mc.member_id = #{memberId} and mc.status = '0' and mc.expiry_end_date > now() and mc.del_flag = '0' and c.del_flag = '0' and mc.coupon_id in
#{id}
order by c.preferential_type desc,c.discount desc,c.money desc
limit #{fetchSize}
