and propValue.id in
#{item}
and prop.id = #{propId}
order by prop.id
order by ifnull(propValue.seq_no,99999),propValue.id

select ifnull(sum(dtl.quantity),0) as productBuyNum, product.limit_buy, product.mcht_id from bu_product product left join bu_order_dtl dtl on dtl.product_id = product.id left join bu_sub_order sub on sub.id = dtl.sub_order_id left join bu_combine_order combineOrder on combineOrder.id = sub.combine_order_id where combineOrder.status in (0,1) and combineOrder.member_id = #{memberId}
and dtl.product_item_id = #{productItemId}
and dtl.activity_id = #{activityId}
and dtl.activity_area_id = #{activityAreaId}
and dtl.product_id = #{productId} and combineOrder.create_date >= date_add(now(), interval -7 day)
and product.del_flag = '0' and dtl.del_flag = '0' and (dtl.product_status is null or dtl.product_status not in ('2','3')) and sub.del_flag = '0' and combineOrder.del_flag = '0'

