		and cart.del_flag = '0'
		and cart.status = '0'
		and cart.member_id = #{memberId}
		and cart.activity_type != '3'
		and item.product_id = #{productId}

			and cart.product_item_id = #{productItemId}


			and cart.activity_id = #{activityId}

