#{listItem}
and co.create_date >= #{limitDate,jdbcType=TIMESTAMP}
) bco LEFT JOIN bu_sub_order so ON bco.id = so.combine_order_id LEFT JOIN bu_order_dtl od ON so.id = od.sub_order_id WHERE so.`status` NOT IN (4, 5) ) od_tmp JOIN bu_single_product_activity spa ON od_tmp.single_product_activity_id = spa.id WHERE spa.type = #{activityType,jdbcType=VARCHAR}

SELECT IFNULL(sum(sc_tmp.quantity), 0) total_quantity FROM ( SELECT sc.quantity, sc.single_product_activity_id FROM bu_shopping_cart sc WHERE sc.member_id IN
#{listItem}
and sc.`status` = '0' and sc.del_flag = '0' ) sc_tmp JOIN bu_single_product_activity spa ON sc_tmp.single_product_activity_id = spa.id WHERE spa.type = #{activityType,jdbcType=VARCHAR}

