, bu_mcht_info mi where p.mcht_id = mi.id and p.del_flag = '0' and p.status = '1' and p.audit_status = '2' and p.sale_type = '1' and mi.`status` = '1' and mi.shop_status = '1' and mi.del_flag = '0' and exists (select mpb.id from bu_mcht_product_brand mpb where mpb.product_brand_id = p.brand_id and mpb.mcht_id = mi.id and mpb.`status` = '1' and mpb.audit_status = '3' and mpb.del_flag = '0') and (p.source = '0' or p.source is null) and p.suit_group like #{suitGroup} and p.min_mall_price >= #{priceMin} and p.min_mall_price <= #{priceMax}
