SELECT p.id, p.mcht_id as mchtId, p.name, p.brand_id as brandId, ( SELECT CONCAT(bpi.sale_price,",",bpi.mall_price,",",bpi.tag_price,",",a.id,",",aa.benefit_point) FROM bu_activity_product ap, bu_activity a, bu_activity_area aa, bu_product_item bpi WHERE ap.product_id = p.id and ap.activity_id = a.id and a.activity_area_id = aa.id and ap.product_id = bpi.product_id and bpi.id = p.min_sale_price_item_id and ap.del_flag = '0' and a.del_flag = '0' and aa.del_flag = '0' and bpi.del_flag = '0' and aa. STATUS = '1' and aa.activity_begin_time <= now() and aa.activity_end_time >= now() and a. STATUS = '6' and ap.coo_audit_status = '2' and ap.refuse_flag = 0 limit 1
) as activityInfo, ( SELECT fc.rule FROM bu_shop_preferential_info spi, bu_full_cut fc WHERE spi.type = '2' and spi.begin_date <= NOW() and spi.end_date > NOW() and spi.`status` = '1' and spi.del_flag = '0' and spi.mcht_id = p.mcht_id and spi.preferential_id = fc.id and fc.del_flag = '0'
) as fullCutRule, p.min_mall_price as mallPrice, p.min_sale_price as salePrice, p.min_tag_price as tagPrice, p.svip_discount as svipDiscount, (select max(c.money) from bu_coupon c where c.type_ids = p.id and c.del_flag='0' and c.coupon_type='4' and c.preferential_type='1' and c.mcht_id = p.mcht_id and c.status='1' and c.rec_begin_date <= now() and c.rec_end_date >= now() AND c.is_integral_turntable = '0' ) as couponAmount, (select sum(pi.stock-pi.locked_amount) from bu_product_item pi where pi.product_id = p.id and pi.del_flag = '0') as stockSum, (select brand.name from bu_product_brand brand where brand.id = p.brand_id and brand.del_flag = '0') as brandName, (SELECT pic.pic FROM bu_product_pic pic WHERE pic.product_id = p.id and pic.del_flag = '0' and pic.is_default = '1' ) AS pic, (select count(1) from bu_comment c where c.del_flag = '0' and c.product_score in(4,5) and (c.is_show is null or c.is_show = '1') and c.product_id = p.id and c.is_append_comment = '0') goodProductScoreCount, (select count(1) from bu_comment c where c.del_flag = '0' and c.is_append_comment='0' and c.is_show='1' and c.product_id = p.id) totalCommentCount FROM (
( SELECT id as product_id FROM bu_product WHERE STATUS = '1' AND audit_status= '2' AND sale_type = '1' AND del_flag = '0' AND product_type_id = #{type3Id} ORDER BY weights DESC LIMIT 40 )
)t_all,bu_product p,bu_mcht_info mi WHERE t_all.product_id = p.id and p.mcht_id = mi.id and p.del_flag = '0' and p.status = '1' and p.audit_status = '2' and p.sale_type = '1' and mi.del_flag = '0' and mi.status = '1' and mi.shop_status = '1' and EXISTS ( SELECT mpb.id FROM bu_mcht_product_brand mpb WHERE mpb.product_brand_id = p.brand_id and mpb.mcht_id = mi.id and mpb.`status` = '1' and mpb.audit_status = '3' and mpb.del_flag = '0' )
and p.id NOT IN
#{productId}
order by p.weights desc
limit #{currentPage} , #{pageSize}

SELECT p.id, p.mcht_id as mchtId, p.name, p.brand_id as brandId, ( SELECT CONCAT(bpi.sale_price,",",bpi.mall_price,",",bpi.tag_price,",",a.id,",",aa.benefit_point) FROM bu_activity_product ap, bu_activity a, bu_activity_area aa, bu_product_item bpi WHERE ap.product_id = p.id and ap.activity_id = a.id and a.activity_area_id = aa.id and ap.product_id = bpi.product_id and bpi.id = p.min_sale_price_item_id and ap.del_flag = '0' and a.del_flag = '0' and aa.del_flag = '0' and bpi.del_flag = '0' and aa. STATUS = '1' and aa.activity_begin_time <= now() and aa.activity_end_time >= now() and a. STATUS = '6' and ap.coo_audit_status = '2' and ap.refuse_flag = 0 limit 1
) as activityInfo, ( SELECT fc.rule FROM bu_shop_preferential_info spi, bu_full_cut fc WHERE spi.type = '2' and spi.begin_date <= NOW() and spi.end_date > NOW() and spi.`status` = '1' and spi.del_flag = '0' and spi.mcht_id = p.mcht_id and spi.preferential_id = fc.id and fc.del_flag = '0'
) as fullCutRule, p.min_mall_price as mallPrice, p.min_sale_price as salePrice, p.min_tag_price as tagPrice, p.svip_discount as svipDiscount, (select max(c.money) from bu_coupon c where c.type_ids = p.id and c.del_flag='0' and c.coupon_type='4' and c.preferential_type='1' and c.mcht_id = p.mcht_id and c.status='1' and c.rec_begin_date <= now() and c.rec_end_date >= now() AND c.is_integral_turntable = '0' ) as couponAmount, (select sum(pi.stock-pi.locked_amount) from bu_product_item pi where pi.product_id = p.id and pi.del_flag = '0') as stockSum, (select brand.name from bu_product_brand brand where brand.id = p.brand_id and brand.del_flag = '0') as brandName, (SELECT pic.pic FROM bu_product_pic pic WHERE pic.product_id = p.id and pic.del_flag = '0' and pic.is_default = '1' ) AS pic, (select count(1) from bu_comment c where c.del_flag = '0' and c.product_score in(4,5) and (c.is_show is null or c.is_show = '1') and c.product_id = p.id and c.is_append_comment = '0') goodProductScoreCount, (select count(1) from bu_comment c where c.del_flag = '0' and c.is_append_comment='0' and c.is_show='1' and c.product_id = p.id) totalCommentCount FROM (
( SELECT id as product_id FROM bu_product WHERE STATUS = '1' AND audit_status= '2' AND sale_type = '1' AND del_flag = '0' AND product_type1_id = #{type1Id} ORDER BY weights DESC LIMIT 20 )
) tt,bu_product p,bu_mcht_info mi WHERE tt.product_id = p.id and p.mcht_id = mi.id and p.del_flag = '0' and p.status = '1' and p.audit_status = '2' and p.sale_type = '1' and mi.del_flag = '0' and mi.status = '1' and mi.shop_status = '1' and EXISTS ( SELECT mpb.id FROM bu_mcht_product_brand mpb WHERE mpb.product_brand_id = p.brand_id and mpb.mcht_id = mi.id and mpb.`status` = '1' and mpb.audit_status = '3' and mpb.del_flag = '0' )
and p.id NOT IN
#{productId}
order by p.weights desc
limit #{currentPage} , #{pageSize}
