SELECT 1 type, msd.id, msd.id mcht_shop_dynamic_id, msd.content, msd.product_ids, msd.weight, msd.audit_date publish_date, msd.forward_count, msd.like_count, mi.id mcht_id, mi.shop_name, '' nick, mi.shop_logo pic, ifnull( ( SELECT mdl. STATUS FROM bu_member_dynamic_like mdl WHERE mdl.del_flag = '0' AND mdl.type = '0' AND mdl.dynamic_id = msd.id AND mdl.member_id = #{memberId} ORDER BY mdl.id DESC LIMIT 1 ), '0' ) like_status, msd.read_count, 1 source, 0 attention_member_id, msd.top_cover FROM bu_mcht_shop_dynamic msd, bu_mcht_info mi WHERE msd.mcht_id = mi.id AND msd.del_flag = '0' AND mi.del_flag = '0' AND msd.audit_status = '1' AND mi. STATUS = '1' AND mi.shop_status = '1' AND mi.id NOT IN ( SELECT mr.remind_id FROM bu_member_remind mr WHERE mr.del_flag='0' AND mr.member_id=#{memberId} AND mr.remind_type='3' AND mr.shielding_dynamics = '1') ORDER BY msd.weight DESC, msd.audit_date DESC
LIMIT #{currentPage}, #{pageSize}
LIMIT 0, 10

SELECT * FROM ( SELECT 1 type, msd.id, msd.id mcht_shop_dynamic_id, msd.content, msd.product_ids, msd.weight, msd.audit_date publish_date, msd.forward_count, msd.like_count, mi.id mcht_id, mi.shop_name, NULL nick, mi.shop_logo pic, ifnull( ( SELECT mdl. STATUS FROM bu_member_dynamic_like mdl WHERE mdl.del_flag = '0' AND mdl.type = '0' AND mdl.dynamic_id = msd.id AND mdl.member_id = #{memberId} ORDER BY mdl.id DESC LIMIT 1 ), '0' ) like_status, msd.read_count, 1 source, 0 attention_member_id, msd.top_cover FROM bu_member_remind mr, bu_mcht_shop_dynamic msd, bu_mcht_info mi WHERE mr.remind_id = mi.id AND msd.mcht_id = mi.id AND mr.del_flag = '0' AND msd.del_flag = '0' AND mi.del_flag = '0' AND mr.remind_type = '3' AND msd.audit_status = '1' AND mi. STATUS = '1' AND mi.shop_status = '1' AND mr.member_id = #{memberId} AND mr.shielding_dynamics = '0' UNION ALL SELECT 2 type, md.id, msd.id mcht_shop_dynamic_id, msd.content, msd.product_ids, msd.weight, md.create_date publish_date, md.forward_count, md.like_count, mi.id mcht_id, mi.shop_name, m.nick, m.pic, ifnull( ( SELECT mdl. STATUS FROM bu_member_dynamic_like mdl WHERE mdl.del_flag = '0' AND mdl.type = '1' AND mdl.dynamic_id = md.id AND mdl.member_id = #{memberId} ORDER BY mdl.id DESC LIMIT 1 ), '0' ) like_status, msd.read_count, 2 source, ma.attention_member_id, msd.top_cover FROM bu_member_attention ma, bu_member_dynamic md, bu_mcht_shop_dynamic msd, bu_mcht_info mi, bu_member_info m WHERE ma.attention_member_id = md.member_id AND md.mcht_shop_dynamic_id = msd.id AND msd.mcht_id = mi.id AND md.member_id = m.id AND ma.del_flag = '0' AND md.del_flag = '0' AND msd.del_flag = '0' AND mi.del_flag = '0' AND m.del_flag = '0' AND msd.audit_status = '1' AND mi. STATUS = '1' AND mi.shop_status = '1' AND ma.member_id = #{memberId} AND ma.shielding_dynamics = '0' UNION ALL SELECT 2 type, md.id, msd.id mcht_shop_dynamic_id, msd.content, msd.product_ids, msd.weight, md.create_date publish_date, md.forward_count, md.like_count, mi.id mcht_id, mi.shop_name, m.nick, m.pic, ifnull( ( SELECT mdl. STATUS FROM bu_member_dynamic_like mdl WHERE mdl.del_flag = '0' AND mdl.type = '1' AND mdl.dynamic_id = md.id AND mdl.member_id = #{memberId} ORDER BY mdl.id DESC LIMIT 1 ), '0' ) like_status, msd.read_count, 3 source, 0 attention_member_id, msd.top_cover FROM bu_member_dynamic md, bu_mcht_shop_dynamic msd, bu_mcht_info mi, bu_member_info m WHERE md.mcht_shop_dynamic_id = msd.id AND msd.mcht_id = mi.id AND md.member_id = m.id AND md.del_flag = '0' AND msd.del_flag = '0' AND mi.del_flag = '0' AND m.del_flag = '0' AND msd.audit_status = '1' AND mi. STATUS = '1' AND mi.shop_status = '1' AND md.member_id = #{memberId} ) A ORDER BY A.weight ASC, A.publish_date DESC
LIMIT #{currentPage}, #{pageSize}
LIMIT 0, 10

SELECT md.id member_dynamic_id, msd.id mcht_shop_dynamic_id, msd.content, msd.product_ids, msd.weight, md.create_date publish_date, md.forward_count, md.like_count, mi.id mcht_id, mi.shop_name, m.nick, m.pic, ifnull( ( SELECT mdl. STATUS FROM bu_member_dynamic_like mdl WHERE mdl.del_flag = '0' AND mdl.type = '1' AND mdl.dynamic_id = md.id AND mdl.member_id = #{memberId} ORDER BY mdl.id DESC LIMIT 1 ), '0' ) like_status, msd.read_count, msd.top_cover FROM bu_member_dynamic md, bu_mcht_shop_dynamic msd, bu_mcht_info mi, bu_member_info m WHERE md.mcht_shop_dynamic_id = msd.id AND msd.mcht_id = mi.id AND md.member_id = m.id AND md.del_flag = '0' AND msd.del_flag = '0' AND mi.del_flag = '0' AND m.del_flag = '0' AND msd.audit_status = '1' AND mi. STATUS = '1' AND mi.shop_status = '1' AND md.member_id = #{friendMemberId} ORDER BY md.create_date DESC
LIMIT #{currentPage}, #{pageSize}
LIMIT 0, 10
