mapper.select:
select addr.id, addr.phone, addr.recipient, addr.address, addr.is_primary, addr.province, province.AREA_NAME as provinceName, addr.city, city.AREA_NAME as cityName, addr.county, county.AREA_NAME as countyName from bu_member_address addr left join bu_p_area province on province.AREA_ID = addr.province left join bu_p_area city on city.AREA_ID = addr.city left join bu_p_area county on county.AREA_ID = addr.county where addr.del_flag = '0' and addr.member_id = #{memberId}
and addr.id = #{addressId}
and addr.is_primary = 'Y'
limit 1
