mapper.select:
select v.*,mi.shop_name,mi.shop_logo,ifnull(ve.like_count,0) as likeCount from bu_video v left join bu_mcht_info mi on v.mcht_id = mi.id left join bu_video_extend ve on v.id = ve.video_id left join bu_mcht_product_type mpt on v.mcht_id = mpt.mcht_id where v.status ='1' and v.del_flag = '0' and mpt.is_main = '1'
and mpt.product_type_id = #{productType1Id}
and mi.status = '1' and mi.del_flag = '0' order by v.total_weicht desc,v.audit_time desc limit #{page.offset},#{page.pageSize}

select v.id as videoId,vp.product_id,p.name,pp.pic as mainImg,p.min_mall_price as mallPrice,mi.status as mchtStatus,mpb.status as mchtProductBrandStatus from bu_video_product vp left join bu_video v on vp.video_id = v.id left join bu_product p on vp.product_id = p.id left join bu_product_pic pp on p.id=pp.product_id left join (select id,status from bu_mcht_info) mi on v.mcht_id=mi.id left join (select id,mcht_id,product_brand_id,status from bu_mcht_product_brand) mpb on v.mcht_id=mpb.mcht_id and p.brand_id=mpb.product_brand_id where vp.del_flag = '0' and v.status ='1' and v.del_flag = '0' and p.status = '1' and p.del_flag = '0' and pp.is_default = '1' and pp.del_flag = '0' and v.id in
#{listItem}
order by vp.seq_no

mapper.sql:
SELECT temp.id FROM ( (SELECT v.id FROM bu_video v LEFT JOIN bu_mcht_info mi ON v.mcht_id = mi.id WHERE v.status ='1' AND v.del_flag = '0' AND v.product_type_id = #{type3} AND mi.status = '1' AND mi.del_flag = '0' AND v.id NOT IN
#{listItem}
ORDER BY RAND() LIMIT #{fetchSize}) UNION (SELECT v.id FROM bu_video v LEFT JOIN bu_product_type pt3 ON v.`product_type_id`=pt3.id AND pt3.del_flag='0' LEFT JOIN bu_mcht_info mi ON v.mcht_id = mi.id WHERE v.status ='1' AND v.del_flag = '0' AND pt3.`parent_id` = #{type2} AND v.product_type_id!=#{type3} AND mi.status = '1' AND mi.del_flag = '0' AND v.id NOT IN
#{listItem}
ORDER BY RAND() LIMIT #{fetchSize}) UNION (SELECT v.id FROM bu_video v LEFT JOIN bu_product_type pt3 ON v.`product_type_id`=pt3.id AND pt3.del_flag='0' LEFT JOIN bu_product_type pt2 ON pt3.`parent_id`=pt2.id AND pt2.del_flag='0' LEFT JOIN bu_mcht_info mi ON v.mcht_id = mi.id WHERE v.status ='1' AND v.del_flag = '0'AND pt2.`parent_id` = #{type1} AND pt3.parent_id!=#{type2} AND mi.status = '1' AND mi.del_flag = '0' AND v.id NOT IN
#{listItem}
ORDER BY RAND() LIMIT #{fetchSize}) UNION (SELECT v.id FROM bu_video v LEFT JOIN bu_product_type pt3 ON v.`product_type_id`=pt3.id AND pt3.del_flag='0' LEFT JOIN bu_product_type pt2 ON pt3.`parent_id`=pt2.id AND pt2.del_flag='0' LEFT JOIN bu_mcht_info mi ON v.mcht_id = mi.id WHERE v.status ='1' AND v.del_flag = '0' AND pt2.parent_id != #{type1} AND mi.status = '1' AND mi.del_flag = '0' AND v.id NOT IN
#{listItem}
ORDER BY RAND() LIMIT #{fetchSize}) ) temp LIMIT #{fetchSize}
